INSERT INTO `areas_informacion` (`id_area`, `nombre_area`, `descripcion_area`) VALUES
	(1, 'Temporal', 'Agrupación o conjunto de elementos de dato que representen conceptos o aspectos de negocio que proveen información que tienen que ver con el manejo del tiempo, por ejemplo: duración, fecha, año, hora, tipo período.'),
	(2, 'Ubicación', 'Agrupación o conjunto de elementos de dato que representen conceptos o aspectos de negocio que proveen información que tienen que ver con la localización espacial (espacio ocupado) de objetos (personas, documentos, edificios, etc.), por ejemplo: coordenadas, dirección, piso, lugar, zona postal.'),
	(3, 'Identificación', 'Agrupación o conjunto de elementos de dato que representen conceptos o aspectos de negocio que proveen información particular, propia y característica de alguien o algo. Dichos elementos de dato permiten la individualización y/o diferenciación de personas, objetos, documentos u otros elementos de dato que son relevantes para la organización, por ejemplo: número de cédula de ciudadanía, número acto administrativo, código único de identificación de entidad pública.'),
	(4, 'Organización', 'Agrupación o conjunto de elementos de dato que representen conceptos o aspectos de negocio que proveen información referente a la institucionalidad de entidades u organizaciones, por ejemplo: código tipo persona jurídica, código tipo sociedad.'),
	(5, 'Personal', 'Agrupación o conjunto de elementos de dato que representen conceptos o aspectos de negocio que proveen información general referente a personas, pero por sí mismo no permite su individualización, por ejemplo: código sexo, código tipo estado conyugal, código pertenencia étnica.'),
	(6, 'Documental', 'Agrupación o conjunto de elementos de dato que representen conceptos o aspectos de negocio que proveen información respecto a la gestión (creación, distribución y archivo) de documentos, por ejemplo: código tipo documento, texto documento, número de páginas.'),
	(7, 'General', 'Agrupación o conjunto de elementos de dato que representen conceptos de información que complementan el uso o la interpretación de otros elementos de dato, y demás que no estén en las otras áreas, por ejemplo: nombre unidad de medida.');
